// src/components/Sidebar.js
import React from 'react';
import { List, ListItem, ListItemIcon, ListItemText } from '@mui/material';
import { Home, Note, Settings, Add } from '@mui/icons-material';
import { Link } from 'react-router-dom';

const Sidebar = ({ isSidebarOpen }) => {
  return (
    <div className={`sidebar h-full bg-gradient-to-r from-gray-800 to-gray-900 text-white border-r transition-width duration-300 ${isSidebarOpen ? 'w-64' : 'w-16'}`}>
      <div className="header p-4 flex items-center justify-center bg-gradient-to-r from-blue-500 to-indigo-600 text-white">
        {isSidebarOpen && <h1 className="text-xl font-bold">Take Note</h1>}
      </div>
      <List>
        <ListItem button component={Link} to="/">
          <ListItemIcon>
            <Home className="text-white" />
          </ListItemIcon>
          {isSidebarOpen && <ListItemText primary="Home" />}
        </ListItem>
        <ListItem button component={Link} to="/notes">
          <ListItemIcon>
            <Note className="text-white" />
          </ListItemIcon>
          {isSidebarOpen && <ListItemText primary="Notes" />}
        </ListItem>
        <ListItem button component={Link} to="/new-note">
          <ListItemIcon>
            <Add className="text-white" />
          </ListItemIcon>
          {isSidebarOpen && <ListItemText primary="New Note" />}
        </ListItem>
        <ListItem button component={Link} to="/settings">
          <ListItemIcon>
            <Settings className="text-white" />
          </ListItemIcon>
          {isSidebarOpen && <ListItemText primary="Settings" />}
        </ListItem>
      </List>
    </div>
  );
};

export default Sidebar;
