import React, { useState, useRef } from 'react';
import { TextField, IconButton, Tooltip } from '@mui/material';
import {
  FormatBold,
  FormatItalic,
  FormatUnderlined,
  Code,
  FormatQuote,
  FormatListBulleted,
  FormatListNumbered
} from '@mui/icons-material';

const NewNote = () => {
  const [content, setContent] = useState('');
  const textAreaRef = useRef(null);

  const handleContentChange = (event) => {
    setContent(event.target.value);
  };

  const handleSave = () => {
    // Handle saving the note
    console.log('Saved content:', content);
  };

  const applyFormatting = (format) => {
    const textArea = textAreaRef.current;
    const start = textArea.selectionStart;
    const end = textArea.selectionEnd;
    const selectedText = content.slice(start, end);

    let formattedText;
    switch (format) {
      case 'bold':
        formattedText = `<strong>${selectedText}</strong>`;
        break;
      case 'italic':
        formattedText = `<em>${selectedText}</em>`;
        break;
      case 'underline':
        formattedText = `<u>${selectedText}</u>`;
        break;
      case 'code':
        formattedText = `<code>${selectedText}</code>`;
        break;
      case 'blockquote':
        formattedText = `<blockquote>${selectedText}</blockquote>`;
        break;
      case 'bulletedList':
        formattedText = `<ul><li>${selectedText}</li></ul>`;
        break;
      case 'numberedList':
        formattedText = `<ol><li>${selectedText}</li></ol>`;
        break;
      default:
        return;
    }

    const newContent = content.slice(0, start) + formattedText + content.slice(end);
    setContent(newContent);

    setTimeout(() => {
      textArea.setSelectionRange(start, start + formattedText.length);
      textArea.focus();
    }, 0);
  };

  return (
    <div className="p-6 flex flex-col h-full">
      <h2 className="text-2xl font-bold mb-4">New Note</h2>
      <div className="flex space-x-2 mb-4">
        <Tooltip title="Bold">
          <IconButton onClick={() => applyFormatting('bold')}>
            <FormatBold />
          </IconButton>
        </Tooltip>
        <Tooltip title="Italic">
          <IconButton onClick={() => applyFormatting('italic')}>
            <FormatItalic />
          </IconButton>
        </Tooltip>
        <Tooltip title="Underline">
          <IconButton onClick={() => applyFormatting('underline')}>
            <FormatUnderlined />
          </IconButton>
        </Tooltip>
        <Tooltip title="Code">
          <IconButton onClick={() => applyFormatting('code')}>
            <Code />
          </IconButton>
        </Tooltip>
        <Tooltip title="Blockquote">
          <IconButton onClick={() => applyFormatting('blockquote')}>
            <FormatQuote />
          </IconButton>
        </Tooltip>
        <Tooltip title="Bulleted List">
          <IconButton onClick={() => applyFormatting('bulletedList')}>
            <FormatListBulleted />
          </IconButton>
        </Tooltip>
        <Tooltip title="Numbered List">
          <IconButton onClick={() => applyFormatting('numberedList')}>
            <FormatListNumbered />
          </IconButton>
        </Tooltip>
      </div>
      <TextField
        variant="outlined"
        multiline
        fullWidth
        rows={20}
        value={content}
        onChange={handleContentChange}
        placeholder="Start typing your note..."
        inputRef={textAreaRef}
        className="flex-grow"
        inputProps={{ style: { whiteSpace: 'pre-wrap' } }}
      />
      <div className="mt-4">
        <button
          onClick={handleSave}
          className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
        >
          Save Note
        </button>
      </div>
      <div
        className="mt-4 border p-4"
        dangerouslySetInnerHTML={{ __html: content }}
      />
    </div>
  );
};

export default NewNote;
