// src/pages/Settings.js
import React, { useState } from 'react';
import { Switch } from '@mui/material';

const Settings = () => {
  const [darkMode, setDarkMode] = useState(false);

  const handleDarkModeToggle = () => {
    setDarkMode(!darkMode);
    // Apply dark mode logic here, e.g., adding/removing dark mode class
    if (!darkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  };

  return (
    <div className="p-6 bg-white dark:bg-gray-800 rounded-lg shadow-md max-w-xl mx-auto mt-10">
      <h2 className="text-2xl font-bold mb-6">Settings</h2>
      <div className="space-y-4">
        <div className="flex justify-between items-center">
          <span className="text-lg dark:text-gray-300">Dark Mode</span>
          <Switch
            checked={darkMode}
            onChange={handleDarkModeToggle}
            color="primary"
          />
        </div>
        {/* Add more settings here */}
        <div className="flex justify-between items-center">
          <span className="text-lg dark:text-gray-300">Setting 2</span>
          <Switch
            checked={true} // Default state
            onChange={() => {}}
            color="primary"
          />
        </div>
        <div className="flex justify-between items-center">
          <span className="text-lg dark:text-gray-300">Setting 3</span>
          <Switch
            checked={false} // Default state
            onChange={() => {}}
            color="primary"
          />
        </div>
      </div>
    </div>
  );
};

export default Settings;
