// src/pages/NewNote.js
import React, { useState } from 'react';
import { TextField, IconButton, Tooltip } from '@mui/material';
import {
  FormatBold,
  FormatItalic,
  FormatUnderlined,
  Code,
  FormatQuote,
  FormatListBulleted,
  FormatListNumbered
} from '@mui/icons-material';

const NewNote = () => {
  const [content, setContent] = useState('');

  const handleContentChange = (event) => {
    setContent(event.target.value);
  };

  const handleSave = () => {
    // Handle saving the note
    console.log('Saved content:', content);
  };

  const insertText = (text) => {
    setContent(content + text);
  };

  return (
    <div className="p-6 flex flex-col h-full">
      <h2 className="text-2xl font-bold mb-4">New Note</h2>
      <div className="flex space-x-2 mb-4">
        <Tooltip title="Bold">
          <IconButton onClick={() => insertText('**bold**')}>
            <FormatBold />
          </IconButton>
        </Tooltip>
        <Tooltip title="Italic">
          <IconButton onClick={() => insertText('*italic*')}>
            <FormatItalic />
          </IconButton>
        </Tooltip>
        <Tooltip title="Underline">
          <IconButton onClick={() => insertText('__underline__')}>
            <FormatUnderlined />
          </IconButton>
        </Tooltip>
        <Tooltip title="Code">
          <IconButton onClick={() => insertText('`code`')}>
            <Code />
          </IconButton>
        </Tooltip>
        <Tooltip title="Blockquote">
          <IconButton onClick={() => insertText('> blockquote')}>
            <FormatQuote />
          </IconButton>
        </Tooltip>
        <Tooltip title="Bulleted List">
          <IconButton onClick={() => insertText('- list item')}>
            <FormatListBulleted />
          </IconButton>
        </Tooltip>
        <Tooltip title="Numbered List">
          <IconButton onClick={() => insertText('1. list item')}>
            <FormatListNumbered />
          </IconButton>
        </Tooltip>
      </div>
      <TextField
        variant="outlined"
        multiline
        fullWidth
        rows={20}
        value={content}
        onChange={handleContentChange}
        placeholder="Start typing your note..."
        className="flex-grow"
      />
      <button
        onClick={handleSave}
        className="mt-4 self-end px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
      >
        Save Note
      </button>
    </div>
  );
};

export default NewNote;
