import React, { useState, useRef } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import { IconButton, Tooltip, Select, MenuItem } from '@mui/material';
import {
  FormatBold,
  FormatItalic,
  FormatUnderlined,
  Code,
  FormatQuote,
  FormatListBulleted,
  FormatListNumbered,
} from '@mui/icons-material';

const NewNote = () => {
  const [content, setContent] = useState('');
  const quillRef = useRef(null);
  const [fontSize, setFontSize] = useState('normal');

  const handleContentChange = (value) => {
    setContent(value);
  };

  const handleSave = () => {
    console.log('Saved content:', content);
  };

  const applyFormatting = (format) => {
    const editor = quillRef.current.getEditor();
    editor.focus();

    switch (format) {
      case 'bold':
        editor.format('bold', !editor.getFormat().bold);
        break;
      case 'italic':
        editor.format('italic', !editor.getFormat().italic);
        break;
      case 'underline':
        editor.format('underline', !editor.getFormat().underline);
        break;
      case 'code':
        editor.format('code-block', !editor.getFormat()['code-block']);
        break;
      case 'blockquote':
        editor.format('blockquote', !editor.getFormat().blockquote);
        break;
      case 'bulletedList':
        editor.format('list', editor.getFormat().list === 'bullet' ? false : 'bullet');
        break;
      case 'numberedList':
        editor.format('list', editor.getFormat().list === 'ordered' ? false : 'ordered');
        break;
      default:
        return;
    }
  };

  const handleFontSizeChange = (event) => {
    const editor = quillRef.current.getEditor();
    const size = event.target.value;
    setFontSize(size);
    editor.format('size', size === 'normal' ? false : size);
  };

  return (
    <div className="p-6 flex flex-col h-full">
      <h2 className="text-2xl font-bold mb-4">New Note</h2>
      <div className="flex space-x-2 mb-4">
        <Tooltip title="Bold">
          <IconButton onClick={() => applyFormatting('bold')}>
            <FormatBold />
          </IconButton>
        </Tooltip>
        <Tooltip title="Italic">
          <IconButton onClick={() => applyFormatting('italic')}>
            <FormatItalic />
          </IconButton>
        </Tooltip>
        <Tooltip title="Underline">
          <IconButton onClick={() => applyFormatting('underline')}>
            <FormatUnderlined />
          </IconButton>
        </Tooltip>
        <Tooltip title="Code">
          <IconButton onClick={() => applyFormatting('code')}>
            <Code />
          </IconButton>
        </Tooltip>
        <Tooltip title="Blockquote">
          <IconButton onClick={() => applyFormatting('blockquote')}>
            <FormatQuote />
          </IconButton>
        </Tooltip>
        <Tooltip title="Bulleted List">
          <IconButton onClick={() => applyFormatting('bulletedList')}>
            <FormatListBulleted />
          </IconButton>
        </Tooltip>
        <Tooltip title="Numbered List">
          <IconButton onClick={() => applyFormatting('numberedList')}>
            <FormatListNumbered />
          </IconButton>
        </Tooltip>
        <Select
          value={fontSize}
          onChange={handleFontSizeChange}
          displayEmpty
          variant="outlined"
          style={{ width: 100 }}
        >
          <MenuItem value="normal">Normal</MenuItem>
          <MenuItem value="small">Small</MenuItem>
          <MenuItem value="large">Large</MenuItem>
          <MenuItem value="huge">Huge</MenuItem>
        </Select>
      </div>
      <div className="flex-grow">
        <ReactQuill
          ref={quillRef}
          value={content}
          onChange={handleContentChange}
          placeholder="Start typing your note..."
          modules={{ toolbar: false }} // Disable default toolbar
          style={{ height: '400px', backgroundColor: 'white' }} // Custom styling
        />
      </div>
      <div className="mt-4">
        <button
          onClick={handleSave}
          className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
        >
          Save Note
        </button>
      </div>
    </div>
  );
};

export default NewNote;
