// src/pages/Settings.js
import React, { useState } from 'react';
import { useTheme } from '../contexts/ThemeContext';
import { Switch, FormControlLabel, Select, MenuItem, FormControl, InputLabel } from '@mui/material';

const Settings = () => {
  const { theme, toggleTheme } = useTheme();
  const [fontSize, setFontSize] = useState('medium');

  const handleFontSizeChange = (event) => {
    setFontSize(event.target.value);
  };

  return (
    <div className="space-y-4">
      <h2 className="text-2xl font-bold">Settings Page</h2>
      <FormControlLabel
        control={<Switch checked={theme === 'dark'} onChange={toggleTheme} />}
        label="Dark Mode"
      />
      <FormControl variant="outlined" className="w-64">
        <InputLabel>Font Size</InputLabel>
        <Select value={fontSize} onChange={handleFontSizeChange} label="Font Size">
          <MenuItem value="small">Small</MenuItem>
          <MenuItem value="medium">Medium</MenuItem>
          <MenuItem value="large">Large</MenuItem>
        </Select>
      </FormControl>
    </div>
  );
};

export default Settings;
