import React, { useState, useRef } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import { IconButton, Tooltip } from '@mui/material';
import {
  FormatBold,
  FormatItalic,
  FormatUnderlined,
  Code,
  FormatQuote,
  FormatListBulleted,
  FormatListNumbered
} from '@mui/icons-material';

const NewNote = () => {
  const [content, setContent] = useState('');
  const quillRef = useRef(null);

  const handleContentChange = (value) => {
    setContent(value);
  };

  const handleSave = () => {
    // Handle saving the note
    console.log('Saved content:', content);
  };

  const applyFormatting = (format) => {
    const editor = quillRef.current.getEditor();
    editor.focus();

    switch (format) {
      case 'bold':
        editor.format('bold', true);
        break;
      case 'italic':
        editor.format('italic', true);
        break;
      case 'underline':
        editor.format('underline', true);
        break;
      case 'code':
        editor.format('code-block', true);
        break;
      case 'blockquote':
        editor.format('blockquote', true);
        break;
      case 'bulletedList':
        editor.format('list', 'bullet');
        break;
      case 'numberedList':
        editor.format('list', 'ordered');
        break;
      default:
        return;
    }
  };

  return (
    <div className="p-6 flex flex-col h-full">
      <h2 className="text-2xl font-bold mb-4">New Note</h2>
      <div className="flex space-x-2 mb-4">
        <Tooltip title="Bold">
          <IconButton onClick={() => applyFormatting('bold')}>
            <FormatBold />
          </IconButton>
        </Tooltip>
        <Tooltip title="Italic">
          <IconButton onClick={() => applyFormatting('italic')}>
            <FormatItalic />
          </IconButton>
        </Tooltip>
        <Tooltip title="Underline">
          <IconButton onClick={() => applyFormatting('underline')}>
            <FormatUnderlined />
          </IconButton>
        </Tooltip>
        <Tooltip title="Code">
          <IconButton onClick={() => applyFormatting('code')}>
            <Code />
          </IconButton>
        </Tooltip>
        <Tooltip title="Blockquote">
          <IconButton onClick={() => applyFormatting('blockquote')}>
            <FormatQuote />
          </IconButton>
        </Tooltip>
        <Tooltip title="Bulleted List">
          <IconButton onClick={() => applyFormatting('bulletedList')}>
            <FormatListBulleted />
          </IconButton>
        </Tooltip>
        <Tooltip title="Numbered List">
          <IconButton onClick={() => applyFormatting('numberedList')}>
            <FormatListNumbered />
          </IconButton>
        </Tooltip>
      </div>
      <ReactQuill
        ref={quillRef}
        theme="snow"
        value={content}
        onChange={handleContentChange}
        placeholder="Start typing your note..."
      />
      <button
        onClick={handleSave}
        className="mt-4 self-end px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
      >
        Save Note
      </button>
    </div>
  );
};

export default NewNote;
